import * as Yup from "yup";

export const loginSchema = Yup.object().shape({
  email: Yup.string()
    // .trim()
    .required("Please enter Email")
    .email("Please enter valid Email")
    .max(50, "Maximum length for Email should be ${max} characters")
    .matches(/^\S*$/, "Please enter valid Email"),
  password: Yup.string()
    .required("Please enter Password")
    .matches(/^\S*$/, "Please enter correct Email or Password")
    .min(6, "Please enter correct Email or Password")
    .max(20, "Please enter correct Email or Password"),
});
