import axios from "axios";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const UserProfile = () => {
  const [userDetail, setUserDetail] = useState([]);
  const { id } = useParams();
  console.log(id);
  useEffect(() => {
    // GET request for remote image in node.js
    axios({
      method: "get",
      url: `https://jsonplaceholder.typicode.com/users/?id=${id}`,
      responseType: "stream",
    }).then(function (response) {
      // console.log(response);
      setUserDetail(response.data);
    });
  }, []);
  return (
    <div className="container my-5">
      <div className="card rounded text-start mb-3">
        <div className="card-header">
          <h5>{userDetail.name}</h5>
        </div>
        <div className="card-body">
          <div className="row">
            <div className="col-md-6">
              <span className="font-weight-bold">User Name: </span>
              <span>{userDetail.username}</span>
            </div>
            <div className="col-md-6">
              <span className="font-weight-bold">Name: </span>
              <span>{userDetail.name}</span>
            </div>
            <div className="col-md-6">
              <span className="font-weight-bold">Email: </span>
              <span>{userDetail.email}</span>
            </div>
            <div className="col-md-12">
              <span className="font-weight-bold">Address: </span>
              <span>{userDetail.address}
                {/* {userDetail.address}, {userDetail.address.street},{" "}
                {userDetail.address.suite}, {userDetail.address.city},{" "}
                {userDetail.address.zipcode} */}
              </span>
            </div>

            {/* 
geo
lat -31.8129
lng 62.5342
phone (254)954-1289
website demarco.info
company
name Keebler LLC
catchPhrase User-centric fault-tolerant solution
bs revolutionize end-to-end systems */}
          </div>
        </div>
        <div className="card-footer text-end">
          <button
            // onClick={() => {
            //   history.push(`/users/${Id}`);
            // }}
            className="btn btn-success"
          >
            View Blogs
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
